<template>
    <section v-show="show" id="cylinder" class="pt-0 pb-0 dark text-center" :class="{active:show}">
        <div class="container-fluid pad-x2">
            <h2 class="">Do you have a Hot Water Cylinder?</h2>

            <div class="row row-centered">
                <div class="col-xs-6 col-md-4 col-lg-20 mb-60 col-centered" v-on:click="setCylinder('yes')">
                    <div class="card shadow-box btn btn-primary btn-sm" style="background-color:transparent">
                        <img src="data:image/svg+xml;base64," alt="Photo" class="card-img-top">
                        <span class="mb-5 mt-5">YES</span>
                    </div>
                </div>
                <div class="col-xs-6 col-md-4 col-lg-20 mb-60 col-centered" v-on:click="setCylinder('yes, remove it!')">
                    <div class="card shadow-box btn btn-primary btn-sm" style="background-color:transparent">
                        <img src="data:image/svg+xml;base64," alt="Photo" class="card-img-top">
                        <span class="mb-5 mt-5">YES, REMOVE IT!</span>
                    </div>
                </div>
                <div class="col-xs-6 col-md-4 col-lg-20 mb-60 col-centered" v-on:click="setCylinder('no')">
                    <div class="card shadow-box btn btn-primary btn-sm" style="background-color:transparent">
                        <img src="data:image/svg+xml;base64," alt="Photo" class="card-img-top">
                        <span class="mb-5 mt-5">NO</span>
                    </div>
                </div>
                <div class="col-xs-6 col-md-4 col-lg-20 mb-60 col-centered" v-on:click="setCylinder('I don\'t know')">
                    <div class="card shadow-box btn btn-primary btn-sm" style="background-color:transparent">
                        <img src="data:image/svg+xml;base64," alt="Photo" class="card-img-top">
                        <span class="mb-5 mt-5">I DON'T KNOW</span>
                    </div>
                </div>
            </div>
            <div class="bg"></div>
        </div>
    </section>
</template>

<script>
    export default {
        props: ['show'],
        methods: {
            setCylinder(i){
                this.$parent.steps.cylinder_step.data = i;
                this.$parent.steps.cylinder_step.show = !this.show;
                this.$parent.steps.packages_step.show = this.show;
            }
        }
    }
</script>
